localparam BMP_CTL =  32'hFFFFC008;          // address for BMP Control
localparam BMP_XLOC = 32'hFFFFC009;         // address for BMP X Location
localparam BMP_YLOC = 32'hFFFFC00A;         // address for BMP Y Location
