cpu iCPU(.clk(clk), .rst_n(rst_n | state), .debug(state), .data_cpu(data_cpu), .waddr_cpu(waddr_cpu), .waddr_out(waddr_out), .data_out(data_out), .halt(LEDR[9]), .joystick_data({busy, 19'b0, joystick_data}));

// Joystick
